template.declaration:
clock timeout_precommit;
clock timeout_commit;

void msg_Broadcast(int tag, int msg_data)
{
    if (tag == Tag_prevote){
        for (i: int[0, N-1]){
            Msg[i].Prevote[Node[nodeID].Round][nodeID] = msg_data; 
        }
    }

    if (tag == Tag_precommit){
        for (i: int[0, N-1]){
            Msg[i].Precommit[Node[nodeID].Round][nodeID] = msg_data; 
        }
    }
    return;
}

bool is23_Any(int tag, int msg_round)
{
    int total_voting_power = 0;
    int sum_voting_power = 0;

    for (i: int[0, N-1]){
        total_voting_power = total_voting_power + Validator[i].Voting_power;
    }

    if (tag == Tag_prevote){
        for (i: int[0, N-1]){
            if (Msg[nodeID].Prevote[msg_round][i] > -1){
                sum_voting_power = sum_voting_power + Validator[i].Voting_power;
            }
        }
        if (1000 * sum_voting_power > 667 * total_voting_power){
            return true;
        }
    }

    if (tag == Tag_precommit){
        for (i: int[0, N-1]){
            if (Msg[nodeID].Precommit[msg_round][i] > -1){
                sum_voting_power = sum_voting_power + Validator[i].Voting_power;
            }
        }
        if (1000 * sum_voting_power > 667 * total_voting_power){
            return true;
        }
    }
    return false;
}

int is23_Block(int tag, int msg_round)
{
    int pivot_data = 0;
    int total_voting_power = 0;
    int sum_voting_power = 0;

    for (i: int[0, N-1]){
        total_voting_power = total_voting_power + Validator[i].Voting_power;
    }

    if (tag == Tag_prevote){
        for (i: int[0, N-1]){
            pivot_data = Msg[nodeID].Prevote[msg_round][i];
            for(j: int[0, N-1]){
                if (pivot_data == Msg[nodeID].Prevote[msg_round][j]){
                    sum_voting_power = sum_voting_power + Validator[j].Voting_power;
                }
            }
            if (1000 * sum_voting_power > 667 * total_voting_power){
                return pivot_data;
            }
            sum_voting_power = 0;
        }
    }

    if (tag == Tag_precommit){
        for (i: int[0, N-1]){
            pivot_data = Msg[nodeID].Precommit[msg_round][i];
            for(j: int[0, N-1]){
                if (pivot_data == Msg[nodeID].Precommit[msg_round][j]){
                    sum_voting_power = sum_voting_power + Validator[j].Voting_power;
                }
            }
            if (1000 * sum_voting_power > 667 * total_voting_power){
                return pivot_data;
            }
            sum_voting_power = 0;
        }
    }
    return -1;
}

bool is23_Maj(int tag, int msg_round)
{
    if (is23_Block(tag, msg_round) > 0) return true;
    else return false;
}

void check_unlock()
{
    if (Node[nodeID].PoLCR != -1 && Node[nodeID].LLR != -1 && Node[nodeID].PoLCR < Node[nodeID].LLR && Node[nodeID].LLR && Node[nodeID].Round){
        if (is23_Maj(Tag_prevote, Node[nodeID].PoLCR) && is23_Block(Tag_prevote, Node[nodeID].PoLCR) != Node[nodeID].Locked_block){
            Node[nodeID].LLR = -1;
            Node[nodeID].Locked_block = 0;
        }
    }
    return;
}

void propose_step()
{
    if (Node[nodeID].LLR != -1){
        Node[nodeID].PoLCR = Node[nodeID].LLR;
        Node[nodeID].Propose_block = Node[nodeID].Locked_block;
    }
    else {
        Node[nodeID].Propose_block = Data_List[nodeID];
    }

    for (i: int[0, N-1]){
        Msg[i].Propose[Node[nodeID].Round] = Node[nodeID].Propose_block;
    }
    return;
}

void proposer_selection()
{
    for (i: n_id){
        if (Node[(Proposal_node + i) % (N-F)].BlockChain.Block_list[node_h] == -1) {
            Proposal_node = (Proposal_node + i) % (N-F);
            return;
        }
    }
    return;
}

void init_set()
{
    Node[nodeID].Round = 0;
    Node[nodeID].LLR = -1;
    Node[nodeID].PoLCR = -1;
    Node[nodeID].Locked_block = 0;
    Node[nodeID].Propose_block = -1;
    for (i: int[0, Max_round-1]){
        Msg[nodeID].Propose[i] = -1;    
        for (j: int[0, N-1]){
            Msg[nodeID].Prevote[i][j] = -1;
            Msg[nodeID].Precommit[i][j] = -1;
        }
    }
}

void new_round()
{
    Node[nodeID].Round++;
    Node[nodeID].PoLCR = -1;
    proposer_selection();
}

void new_height()
{
    Node[nodeID].BlockChain.Block_list[Node[nodeID].BlockChain.Height] = is23_Block(Tag_precommit, Node[nodeID].Round);
    Node[nodeID].BlockChain.Height++;
    init_set();
    proposer_selection();
}
